        }

        private async void MobileConnect()
        {
            if (PlayerPrefs.HasKey("Account"))
            {
                this.account = PlayerPrefs.GetString("Account");
                onConnected?.Invoke();
                return;
            }

            // get current timestamp
            int timestamp = (int) (System.DateTime.UtcNow.Subtract(new System.DateTime(1970, 1, 1))).TotalSeconds;
            // set expiration time
            int expirationTime = timestamp + 60;
            // set message
            string message = expirationTime.ToString();
            // sign message
            string signature = await Web3Wallet.Sign(message);
            string account = String.Empty; //0x13751b50427e536A6fF9ee2dd9a6A300176Dad42
//#if UNITY_EDITOR
            //account = Web3PrivateKey.Address("0x83c0b538ce74a97527a5ce64f65ee5766de7c9b73e48ca86180014718a90903f");
//#else
            // verify account
             account = await EVM.Verify(message, signature);
//#endif
            int now = (int) (System.DateTime.UtcNow.Subtract(new System.DateTime(1970, 1, 1))).TotalSeconds;
            // validate
            if (account.Length == 42 && expirationTime >= now)
            {
                // save account
                this.account = account;
                PlayerPrefs.SetString("Account", account);
                onConnected?.Invoke();
            }
        }

        public async UniTask<string> ShowBalance(string chain = Chain.InUse, string network = Network.InUse)
        {
            string balance = await EVM.BalanceOf(chain, network, this.account);
            return balance;
        }

        public async UniTask<int> GetBalanceByContract(
            string chain = Chain.InUse,
            string network = Network.InUse,
            string contract = Contracts.MotoDEXnft)
        {
            int balance = await ERC721.BalanceOf(chain, network, contract, account);
            return balance;
        }

        public async UniTask<string> GetAllErc721(
            string chain = Chain.InUse,
            string network = Network.InUse,
            string contract = Contracts.MotoDEXnft)
        {
            string response = await EVM.AllErc721(chain, network, account, contract);
            return response;
        }

