            string chain = Chain.InUse,
            string network = Network.InUse,
            string contract = Contracts.MotoDEXnft)
        {
            string uri = await ERC721.URI(chain, network, contract, tokenId);
            return uri;
        }
        
        public async UniTask<IDictionary<string, string>> GetAllTokenIdsAndOwners()
        {
            IDictionary<string, string> response = await AddedCheck.NFTAddedCheck(account);
            return response;
        }

        private async void OnConnected()
        {
            account = ConnectAccount();
            while (account == "")
            {
                await new WaitForSeconds(1f);
                account = ConnectAccount();
            }
            
            PlayerPrefs.SetString("Account", account);
            SetConnectAccount("");
            onConnected?.Invoke();
        }
    }
}